import { useState, useEffect } from "react"

export const UpdateAuthor = ({authors}) => {
    const [ name, setName ] = useState("")
    const [ born, setBorn ] = useState("")

    return(
        <div>
            <h2>Set birthyear</h2>
            <form>
                <div>
                    Author: 
                    <input type="name" value={name} onChange={(event) => setName(event.target.value)}/>
                </div>
                <div>
                    Born:
                    <input type="number" value={born} onChange={(event) => setBorn(event.target.value)} />
                </div>

            </form>
        </div>
    )
}
