import { useState } from 'react'
import { useMutation, useQuery } from '@apollo/client'
import { ADD_BOOK, ALL_BOOKS } from '../queries'

const NewBook = (props) => {
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [published, setPublished] = useState('')
  const [genre, setGenre] = useState('')
  const [genres, setGenres] = useState([])
  const [error, setError] = useState("")

  const [createBook] = useMutation(ADD_BOOK, {
    refetchQueries: [{ query: ADD_BOOK}]
  });

  const result = useQuery(ALL_BOOKS)

  if (!props.show) {
    return null
  }

  const submit = async (event) => {
    event.preventDefault()

    const existingBook = result.data.allBooks.some(book => book.title === title);
    if (existingBook) {
      setError('Book with the same title already exists');
      return;
    }

    createBook({ variables: { title, published: parseInt(published), author, genres }});

    setTitle('');
    setPublished('');
    setAuthor('');
    setGenres([]);
    setGenre('');


    setTitle('')
    setPublished('')
    setAuthor('')
    setGenres([])
    setGenre('')
  }

  const addGenre = () => {
    setGenres(genres.concat(genre))
    setGenre('')
  }

  return (
    <div>
      <form onSubmit={submit}>
        <div>
          title
          <input
            value={title}
            onChange={({ target }) => setTitle(target.value)}
          />
        </div>
        <div>
          author
          <input
            value={author}
            onChange={({ target }) => setAuthor(target.value)}
          />
        </div>
        <div>
          published
          <input
            type="number"
            value={published}
            onChange={({ target }) => setPublished(target.value)}
          />
        </div>
        <div>
          <input
            value={genre}
            onChange={({ target }) => setGenre(target.value)}
          />
          <button onClick={addGenre} type="button">
            add genre
          </button>
        </div>
        <div>genres: {genres.join(' ')}</div>
        <button type="submit">create book</button>
      </form>
    </div>
  )
}

export default NewBook
